downcased = handle_Twitter.map(&:downcase)
total_aude = 0
downcased.each do |i|
  if i.include?("aude")
    total_aude += 1
  else
    total_aude = total_aude
  end
end
puts "Q3 : Les lettres aude sont présentes dans #{total_aude} pseudo ---"

